 viewModel.fetchNotes(context, videoId)
 }

 LaunchedEffect(noteAdded) {
 if (noteAdded == true) {
 Log.d("ffkgh","hgfikuyhfg")
 viewModel.fetchNotes(context, videoId)
 showAddNoteForm = false
 }
 }

 Scaffold(
