 @Composable
 fun DisplayTopThree(
 topUsers: List<LeaderData>,
 onUserClick: (LeaderData) -> Unit
 ) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.primaryContainer.copy(alpha = 0.7f)
 ),
 shape = RoundedCornerShape(16.dp)
 ) {
 Column(
 modifier = Modifier.padding(16.dp)
 ) {
 Text(
 text = "Top Learners",
 style = MaterialTheme.typography.titleLarge,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 16.dp),
 color = MaterialTheme.colorScheme.onPrimaryContainer
 )

 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceEvenly,
 verticalAlignment = Alignment.CenterVertically
 ) {
 // Reorder to show 2nd, 1st, 3rd
 val orderedTopThree = topUsers.sortedBy {
 when (it.rank) {
 1 -> 1
 2 -> 0 // 2nd place comes first (left side)
 3 -> 2 // 3rd place comes last (right side)
 else -> it.rank
 }
 }

 orderedTopThree.forEachIndexed { index, user ->
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 modifier = Modifier
 .weight(1f)
 .clickable { onUserClick(user) }
 ) {
 Box(contentAlignment = Alignment.Center) {
 // Profile Image
 Box(
 modifier = Modifier
 .size(if (user.rank == 1) 100.dp else 80.dp)
 .clip(CircleShape)
 .background(
 when (user.rank) {
 1 -> Color(0xFFFFD700) // Gold
 2 -> Color(0xFFC0C0C0) // Silver
 3 -> Color(0xFFCD7F32) // Bronze
 else -> MaterialTheme.colorScheme.surfaceVariant
 }
 ),
 contentAlignment = Alignment.Center
 ) {
 // Here you would use an Image composable with the user's image
 // For now, show an icon if no image is available
 Icon(
 imageVector = Icons.Filled.Info,
 contentDescription = "User Avatar",
 modifier = Modifier.size(if (user.rank == 1) 50.dp else 40.dp),
 tint = Color.White
 )
 }

 // Rank Badge
 Box(
 modifier = Modifier
 .size(if (user.rank == 1) 35.dp else 30.dp)
 .clip(CircleShape)
 .background(MaterialTheme.colorScheme.primary)
 .align(Alignment.BottomEnd),
 contentAlignment = Alignment.Center
 ) {
 Text(
 text = "${user.rank}",
 color = Color.White,
 fontWeight = FontWeight.Bold,
 fontSize = if (user.rank == 1) 18.sp else 16.sp
 )
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // User Name
 Text(
 text = user.fullName,
 fontWeight = FontWeight.SemiBold,
 fontSize = if (user.rank == 1) 16.sp else 14.sp,
 textAlign = TextAlign.Center,
 maxLines = 2,
 modifier = Modifier
 .width(if (user.rank == 1) 120.dp else 100.dp)
 .padding(horizontal = 4.dp)
 )

 // Coin Count
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.Center
 ) {
 Text(
 text = "${user.coins}",
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary,
 fontSize = if (user.rank == 1) 16.sp else 14.sp
 )
 Text(
 text = " ",
 fontSize = if (user.rank == 1) 16.sp else 14.sp
 )
 }
 }
 }
 }
 }
 }
 }
