 verticalAlignment = Alignment.CenterVertically
 ) {
 // Reorder to show 2nd, 1st, 3rd
 val orderedTopThree = topUsers.sortedBy {
 when (it.rank) {
 1 -> 1
 2 -> 0 // 2nd place comes first (left side)
 3 -> 2 // 3rd place comes last (right side)
 else -> it.rank
 }
 }

 orderedTopThree.forEachIndexed { index, user ->
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 modifier = Modifier
 .weight(1f)
 .clickable { onUserClick(user) }
 ) {
