 when (user.rank) {
 1 -> Color(0xFFFFD700) // Gold
 2 -> Color(0xFFC0C0C0) // Silver
 3 -> Color(0xFFCD7F32) // Bronze
 else -> MaterialTheme.colorScheme.surfaceVariant
 }
 ),
 contentAlignment = Alignment.Center
 ) {
