 style = MaterialTheme.typography.titleLarge,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 16.dp),
 color = MaterialTheme.colorScheme.onPrimaryContainer
 )

 // Create scroll state for LazyRow
 val coroutineScope = rememberCoroutineScope()
 val listState = rememberLazyListState()
 val canScrollLeft = remember {
 derivedStateOf {
 listState.firstVisibleItemIndex > 0 || listState.firstVisibleItemScrollOffset > 0
 }
 }.value
 val canScrollRight = remember {
 derivedStateOf {
 val layoutInfo = listState.layoutInfo
 val lastVisibleItemIndex =
 layoutInfo.visibleItemsInfo.lastOrNull()?.index ?: -1
 lastVisibleItemIndex < topUsers.size - 1
 }
 }.value

 Box(modifier = Modifier.fillMaxWidth()) {
 LazyRow(
 modifier = Modifier
 .fillMaxWidth()
 .height(200.dp),
 state = listState,
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 contentPadding = PaddingValues(horizontal = 4.dp)
 ) {
 items(topUsers) { user ->
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 modifier = Modifier
 .width(80.dp)
 .clickable { onUserClick(user) }
 ) {
 Box(contentAlignment = Alignment.Center) {
 // Profile Image - remove gradient border for top 3
 Box(
 modifier = Modifier
 .size(64.dp)
 .clip(CircleShape)
 .background(MaterialTheme.colorScheme.tertiary),
 contentAlignment = Alignment.Center
 ) {
 Icon(
 imageVector = Icons.Filled.AccountCircle,
 contentDescription = "User Avatar",
 modifier = Modifier.size(32.dp),
 tint = MaterialTheme.colorScheme.onTertiary
 )
 }

 // Medal for top 3, rank badge for others
 if (user.rank <= 3) {
 val medalResource = when (user.rank) {
 1 -> R.drawable.gold
 2 -> R.drawable.silver
 3 -> R.drawable.bronze
 else -> R.drawable.coin// fallback, shouldn't happen
 }

 Icon(
 painter = painterResource(medalResource),
 contentDescription = "Medal",
 modifier = Modifier
 .size(28.dp)
 .align(Alignment.BottomEnd),
 tint = Color.Unspecified
 )
 } else {
 // Rank Badge for positions beyond top 3
 Box(
 modifier = Modifier
 .size(24.dp)
 .clip(CircleShape)
 .background(MaterialTheme.colorScheme.primary)
 .align(Alignment.BottomEnd),
 contentAlignment = Alignment.Center
 ) {
 Text(
 text = "${user.rank}",
 color = MaterialTheme.colorScheme.background,
 fontWeight = FontWeight.Bold,
 fontSize = 12.sp
 )
 }
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // User Name
 Text(
 text = user.fullName,
 style = MaterialTheme.typography.labelMedium,
 fontWeight = FontWeight.SemiBold,
 textAlign = TextAlign.Center,
 maxLines = 2,
 overflow = TextOverflow.Ellipsis,
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 2.dp)
 )

 Spacer(modifier = Modifier.height(4.dp))

 // Coin Count
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.Center
 ) {
 Text(
