fun formatDate(dateString: String): String {
 return try {
 val inputFormat = SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SSS'Z'", Locale.US)
 val outputFormat = SimpleDateFormat("MMM dd, yyyy 'at' hh:mm a", Locale.US)
 val date = inputFormat.parse(dateString)
 outputFormat.format(date ?: Date())
 } catch (e: Exception) {
 dateString
 }
}

@Composable
fun NotesScreen(
 videoId: String,
 viewModel: CourseViewModel,
 context: Context = LocalContext.current
) {
 val notes by viewModel.notes.observeAsState(emptyList())
 val isLoading by viewModel.isLoading.observeAsState(false)
 val noteError by viewModel.noteError.observeAsState()
 val noteAdded by viewModel.noteAddSuccess.observeAsState()

 var showAddNoteForm by rememberSaveable { mutableStateOf(false) }
 var showEditNoteForm by rememberSaveable { mutableStateOf(false) }
 var editingNote by remember { mutableStateOf<Note?>(null) }
 var title by rememberSaveable { mutableStateOf("") }
 var content by rememberSaveable { mutableStateOf("") }
 var showDeleteDialog by remember { mutableStateOf(false) }
 var noteToDelete by remember { mutableStateOf<Note?>(null) }

 LaunchedEffect(videoId) {
 viewModel.fetchNotes(context, videoId)
 }

 LaunchedEffect(noteAdded) {
 if (noteAdded == true) {
 viewModel.fetchNotes(context, videoId)
 showAddNoteForm = false
 showEditNoteForm = false
 editingNote = null
 }
 }

 // Delete confirmation dialog
 if (showDeleteDialog && noteToDelete != null) {
 AlertDialog(
 onDismissRequest = { showDeleteDialog = false },
 title = { Text("Delete Note") },
 text = { Text("Are you sure you want to delete this note? This action cannot be undone.") },
 confirmButton = {
 TextButton(
 onClick = {
 noteToDelete?.let { note ->
 viewModel.deleteNote(context, note._id, videoId)
 }
 showDeleteDialog = false
 noteToDelete = null
 }
 ) {
 Text("Delete", color = MaterialTheme.colorScheme.error)
 }
 },
 dismissButton = {
 TextButton(onClick = { showDeleteDialog = false }) {
 Text("Cancel")
 }
 }
 )
 }

 Scaffold(
 floatingActionButton = {
 if (!showAddNoteForm && !showEditNoteForm) {
 FloatingActionButton(
 onClick = { showAddNoteForm = true },
 containerColor = MaterialTheme.colorScheme.primaryContainer
 ) {
 Icon(
 Icons.Default.Add,
 contentDescription = "Add Note",
 modifier = Modifier.size(32.dp)
 )
 }
 }
 }
 ) { paddingValues ->
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 ) {
 if (isLoading) {
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(200.dp),
 contentAlignment = Alignment.Center
 ) {
 Column(horizontalAlignment = Alignment.CenterHorizontally) {
 CircularProgressIndicator()
 Spacer(modifier = Modifier.height(16.dp))
 Text("Loading notes...", style = MaterialTheme.typography.bodyMedium)
 }
 }
 } else if (notes.isEmpty() && !showAddNoteForm && !showEditNoteForm) {
 EmptyNotesPlaceholder(onClick = { showAddNoteForm = true })
 } else {
 LazyColumn(
 modifier = Modifier.weight(1f),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 item {
 Spacer(modifier = Modifier.height(8.dp))
 Text(
 "${notes.size} note${if (notes.size != 1) "s" else ""}",
 style = MaterialTheme.typography.labelLarge,
 color = MaterialTheme.colorScheme.primary
 )
 }
 items(notes) { note ->
 NoteCard(
 note = note,
 onEdit = { noteToEdit ->
 editingNote = noteToEdit
 title = noteToEdit.title
 content = noteToEdit.content
 showEditNoteForm = true
 },
 onDelete = { note ->
 noteToDelete = note
 showDeleteDialog = true
 }
 )
 }
 item {
 Spacer(modifier = Modifier.height(8.dp))
 }
 }
 }

 AnimatedVisibility(
 visible = showAddNoteForm,
 enter = fadeIn() + expandVertically(),
 exit = fadeOut() + shrinkVertically()
 ) {
 AddNoteForm(
 title = title,
 onTitleChange = { title = it },
 content = content,
 onContentChange = { content = it },
 onAddNote = {
 viewModel.addNote(context, videoId, title, content)
 },
 onCancel = {
 showAddNoteForm = false
 title = ""
 content = ""
 },
 error = noteError,
 isEditing = false
 )
 }

 AnimatedVisibility(
 visible = showEditNoteForm,
 enter = fadeIn() + expandVertically(),
 exit = fadeOut() + shrinkVertically()
 ) {
 AddNoteForm(
 title = title,
 onTitleChange = { title = it },
 content = content,
 onContentChange = { content = it },
 onAddNote = {
 editingNote?.let { note ->
 viewModel.editNote(context, note._id, title, content, videoId)
 }
 },
 onCancel = {
 showEditNoteForm = false
 editingNote = null
 title = ""
 content = ""
 },
 error = noteError,
 isEditing = true
 )
 }
 }
 }
}

