fun signUp(
 fullName: String,
 email: String,
 password: String,
 phoneNumber: String,
 discordId: String,
 role: String,
 onStateChange: (Resource<String>) -> Unit
) {
 onStateChange(Resource.Loading)

 val url = "https://szuumq8b3e.execute-api.ap-south-1.amazonaws.com/prod/api/auth/signup"
 val client = OkHttpClient()

 val json = JSONObject().apply {
 put("fullName", fullName)
 put("email", email)
 put("password", password)
 put("phoneNumber", phoneNumber)
 put("discordId", discordId)
 put("role", role)
 }

 val requestBody = json.toString().toRequestBody("application/json".toMediaTypeOrNull())
 val request = Request.Builder()
 .url(url)
 .post(requestBody)
 .build()

 try {
 client.newCall(request).execute().use { response ->
 val responseBody = response.body?.string()

 if (response.isSuccessful) {
 println("Signup Success Response: $responseBody")
 onStateChange(Resource.Success("User created successfully"))
 } else {
 println("Signup Error Response: $responseBody")
 println("Response Code: ${response.code}")
 println("Response Message: ${response.message}")

 onStateChange(Resource.Error("Sign up failed: $responseBody"))
 }
 }
 } catch (e: Exception) {
 println("Signup Exception: ${e.message}")
 onStateChange(Resource.Error("Exception: ${e.message}"))
 }
}

