 // Optimistically update the note status locally like the JavaScript code
 val currentNotes = _notes.value?.toMutableList() ?: mutableListOf()
 val noteIndex = currentNotes.indexOfFirst { it._id == noteId }
 if (noteIndex >= 0) {
 currentNotes[noteIndex] = currentNotes[noteIndex].copy(status = "requested")
 _notes.postValue(currentNotes)
 }
 // Still fetch notes to ensure consistency
 fetchNotes(context, videoId)
 } else {
 _noteError.postValue("Failed to change note status: ${response.message()}")
 }
 } else {
 _noteError.postValue("Auth token missing")
 }
 } catch (e: Exception) {
 _noteError.postValue("Error changing note status: ${e.localizedMessage}")
 } finally {
 _isLoading.value = false
 }
 }
