 withContext(Dispatchers.IO) {
 val auth = getAuthToken(context)
 if (auth != null) {
 val response = ApiClient.getInstance(auth).watchedVideo(videoId)

 if (response.isSuccessful) {
 onSuccess.invoke()
 Log.d("TAG", "Video marked as watched: $videoId")
