 @Composable
 fun StarRating(
 rating: Int,
 onRatingChanged: (Int) -> Unit,
 modifier: Modifier = Modifier
 ) {
 Row(modifier = modifier) {
 for (i in 1..5) {
 Icon(
 imageVector = if (i <= rating) Icons.Filled.Star else Icons.Outlined.Star,
 contentDescription = "Star $i",
 tint = if (i <= rating) gold else MaterialTheme.colorScheme.primary.copy(alpha = 0.5f),
 modifier = Modifier
 .size(24.dp)
 .clickable {
 onRatingChanged(i)
 }
 )
 }
 }
 }

 @SuppressLint("RememberReturnType")
 @Composable
 fun Lecture(
 courseData: Resource<CourseDetailsResponse?>,
 onVideoSelected: (String) -> Unit,
 viewModel: CourseViewModel,
 context: Context,
 ) {
 when (courseData) {
 is Resource.Loading -> {
 CircularProgressIndicator(
 modifier = Modifier
 .fillMaxWidth()
 .wrapContentSize()
 )
 }

 is Resource.Error -> {
 Text(
 "Error loading course data",
 modifier = Modifier
 .fillMaxWidth()
 .wrapContentSize()
 )
 }

 is Resource.Success -> {
 val videos = courseData.data?.videos

 if (videos.isNullOrEmpty()) {
 Text(
 "No lectures available",
 modifier = Modifier
 .fillMaxWidth()
 .wrapContentSize()
 )
 } else {
 LazyColumn(
 modifier = Modifier
 .fillMaxSize()

 )
 {
 videos.forEach { (lectureNumber, videoList) ->
 item {
 var expanded by remember { mutableStateOf(false) }

 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(5.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 4.dp),
 colors= CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.primaryContainer
 )
 ) {
 Column {
 val allVideosWatched = videoList.all { it.watched }

 Text(
 text = buildAnnotatedString {
 append("Lecture $lectureNumber")
 if (allVideosWatched) {
 withStyle(
 style = SpanStyle(
 fontStyle = FontStyle.Italic,
 fontWeight = MaterialTheme.typography.bodySmall.fontWeight,
 fontSize = MaterialTheme.typography.bodySmall.fontSize,
 color = MaterialTheme.colorScheme.secondary
 )
 ) {
 append(" - Completed")
 }
 }
 },
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.SemiBold,
 color = MaterialTheme.colorScheme.secondary,
 modifier = Modifier
 .fillMaxWidth()
 .clickable { expanded = !expanded }
 .padding(16.dp)
 )

 AnimatedVisibility(visible = expanded) {
 Column {
 videoList.forEachIndexed { index, video ->
 if (index > 0) {
 HorizontalDivider(
 modifier = Modifier
 .fillMaxWidth()
 .padding(
 vertical = 3.dp,
 horizontal = 2.dp
 ),
 thickness = 1.dp,
 color = MaterialTheme.colorScheme.tertiary
 )
 }
 Card(
 Modifier
 .padding(5.dp)
 .fillMaxWidth()
 .clickable {
 onVideoSelected(video.id)
 },
 colors = CardDefaults.cardColors(
 containerColor = Color.Transparent
 )
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.Start
 ) {
 var check by remember {mutableStateOf(video.watched)}

 Checkbox(
 checked = check,
 onCheckedChange = { isChecked ->
 check = isChecked
 if (isChecked) {
 viewModel.markWatched(
 context,
 video.id,
 onSuccess = {
 video.watched = true
 })
 } else {
 viewModel.unmarkWatched(
 context,
 video.id,
 onSuccess = {
 video.watched =
 false
 })
 }
 },
 colors = CheckboxDefaults.colors(
 checkedColor = MaterialTheme.colorScheme.onPrimaryContainer
 )
 )

 Text(
 text = "${video.videoNumber}. ${video.title} - ${video.duration}",
 style = MaterialTheme.typography.bodyMedium,
 color = MaterialTheme.colorScheme.onPrimaryContainer,
 modifier = Modifier.padding(
 start = 20.dp,
 top = 4.dp,
 bottom = 4.dp
 )
 )
 }

 }
 }

 }
 }
 }
 }
 }
 }
 }
 }
 }


 }
 }

 @Composable
 fun RecommendationScreen(
 viewModel: CourseViewModel,
 lifecycleOwner: LifecycleOwner = LocalLifecycleOwner.current,
 videoId: String,
 context: Context,
 modifier: Modifier = Modifier,
 onVideoSelected: (String) -> Unit
 ) {
 val relatedVideos by viewModel.relatedVideos.observeAsState(emptyList())
 val isLoading by viewModel.isLoading.observeAsState(false)
 val lazyState = rememberLazyGridState()
 val configuration = LocalConfiguration.current
 val isLandscape = configuration.orientation == Configuration.ORIENTATION_LANDSCAPE

 val shouldLoadMore = remember {
 derivedStateOf {
 val lastVisibleItemIndex =
 lazyState.layoutInfo.visibleItemsInfo.lastOrNull()?.index ?: -1
 lastVisibleItemIndex >= relatedVideos.size - 1
 }
 }

 LaunchedEffect(videoId) {
 Log.d("Fetchingdat", "first time")
 viewModel.loadRecommendations(videoId, context, true)
 }
 DisposableEffect(lifecycleOwner) {
 onDispose {
 viewModel.reloadRecommendation()
 }
 }
 LaunchedEffect(shouldLoadMore.value) {
 Log.d("Fetchingdat", "pagination time")

 if (shouldLoadMore.value && !isLoading) {
 viewModel.loadRecommendations(videoId, context, false)
 }
 }


 Box(modifier = modifier.fillMaxSize()) {
 if (relatedVideos.isEmpty() && !isLoading) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(32.dp)
 .align(Alignment.Center),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Center
 ) {
 Icon(
 imageVector = Icons.Default.Info,
 contentDescription = "null icon",
 modifier = Modifier.size(64.dp),
 tint = MaterialTheme.colorScheme.primary.copy(alpha = 0.6f)
 )

 Spacer(modifier = Modifier.height(16.dp))

 Text(
 text = "No recommendations available",
 style = MaterialTheme.typography.titleMedium,
 textAlign = TextAlign.Center
 )

 Spacer(modifier = Modifier.height(8.dp))

 Text(
 text = "Try watching more videos to get personalized recommendations",
 style = MaterialTheme.typography.bodyMedium,
 textAlign = TextAlign.Center,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }
 } else {
 LazyVerticalGrid(
 columns = GridCells.Fixed(if (isLandscape) 3 else 2),
 state = lazyState,
 contentPadding = PaddingValues(16.dp),
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 items(relatedVideos.size) { index ->
 VideoItem(relatedVideos[index], context, onVideoSelected)
 }

 if (isLoading) {
 item {
 CircularProgressIndicator(
 modifier = Modifier
 .fillMaxWidth()
 .padding(16.dp)
 .wrapContentSize()
 )
 }
 }
 }
 }
 }
 }

 @Composable
 fun VideoItem(
 video: RecommendationVideo,
 context: Context,
 onVideoSelected: (String) -> Unit
 ) {
 val videoId = extractVideoId(video.url)
 val thumbnailUrl = "https://img.youtube.com/vi/$videoId/0.jpg"

 val cardHeight = 280.dp
 val thumbnailHeight = 140.dp

 Card(
 modifier = Modifier
 .fillMaxWidth()
 .height(cardHeight)
 .clickable { onVideoSelected(video._id) },
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.primaryContainer
 ),
 shape = RoundedCornerShape(8.dp)
 ) {
 Column(
 modifier = Modifier.fillMaxSize()
 ) {
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(thumbnailHeight)
 ) {
 AsyncImage(
 model = ImageRequest.Builder(context)
 .data(thumbnailUrl)
 .crossfade(true)
 .build(),
 contentDescription = "Thumbnail for ${video.title}",
 contentScale = ContentScale.Crop,
 modifier = Modifier.fillMaxSize()
 )

 if (video.watched) {
 Box(
 modifier = Modifier
 .align(Alignment.TopEnd)
 .padding(8.dp)
 .size(24.dp)
 .background(
 color = MaterialTheme.colorScheme.primary,
 shape = CircleShape
 )
 ) {
 Icon(
 imageVector = Icons.Default.CheckCircle,
 contentDescription = "Watched",
 tint = Color.White,
 modifier = Modifier
 .align(Alignment.Center)
 .size(16.dp)
 )
 }
 }
 }

 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(12.dp)
 .weight(1f),
 verticalArrangement = Arrangement.SpaceBetween
 ) {
 Text(
 text = video.title,
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.SemiBold,
 maxLines = 2,
 overflow = TextOverflow.Ellipsis
 )

 Box(
 modifier = Modifier
 .fillMaxWidth()
 .padding(top = 8.dp)
 .clip(RoundedCornerShape(16.dp))
 .height(24.dp)
 .background(
 color = if (video.watched)
 green
 else
 red,
 shape = RoundedCornerShape(4.dp)
 ),
 contentAlignment = Alignment.Center
 ) {
 Text(
 text = if (video.watched) "Watched" else "Unwatched",
 color = MaterialTheme.colorScheme.onSurface,
 style = MaterialTheme.typography.labelMedium,
 fontWeight = FontWeight.Medium
 )
 }
 }
 }
 }
 }

 fun extractVideoId(url: String): String {
 return when {
 url.contains("youtu.be/") -> url.substringAfter("youtu.be/").takeWhile { it != '?' }
 url.contains("youtube.com/watch?v=") -> url.substringAfter("v=").takeWhile { it != '&' }
 url.contains("youtube.com/embed/") -> url.substringAfter("embed/")
 .takeWhile { it != '?' }

 else -> url
 }
 }
}

